import React from 'react';

import './User.css';

const User = ({ user }) => {
    return ( 
            <tr className='user-container'>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                    <div className='buttons-container'>
                        <button className='remover-user-button'>X</button>
                        <button className='remover-user-button'>X</button>
                    </div>
                </td>
            </tr>
     );
}

export default User;

<div className='task-container' style={task.completed ? {borderLeft: '6px solid chartreuse'}: {}}>  
<div className='task-title' onClick={() => handleTaskClick(task.id)}>
    {task.title}    
</div>
{/* Icones */}
<div className='buttons-container'>
    <button className='remove-task' onClick={() => handleTaskRemove(task.id)}>
        <CgClose />
    </button>
    <button className='info-task' onClick={handleTaskInfoClick}>
        <CgInfo />
    </button>
</div>
</div>
