import React, { useState,  useEffect  } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { BrowserRouter as  Router, Route, Routes} from 'react-router-dom';
import axios from 'axios';
import './App.css';
import AddUser from './components/AddUser';
import Users from './components/Users';
import UserInfo from './components/UserInfo';
import Header from './components/Header';


const App = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      const {data} = await axios.get('https://jsonplaceholder.typicode.com/users');
      console.log(data);  
      setUsers(data)
    };

    fetchUsers();
  }, [])
  const handleUserAdd = (nameUser, emailUser, websiteUser, telefoneUser) => {
      const  newUsers = [...users, {
        id: uuidv4(),
        name: nameUser,
        email: emailUser,
        phone: telefoneUser,
        website: websiteUser
      },
    ];
    setUsers(newUsers)
  }

  const handleUserRemove = (userId) => {
    const newUsers = users.filter(user => user.id !== userId)
    setUsers(newUsers)
  };
  return (
    <>
      <div className='title'>
        <h1>Teste Pr치tico Lista de Usu치rios - Linear</h1>
        <p>Feito por carinho por </p>
      </div>
      <Router>
      <div className='container'>
        <Routes>
          <Route path="/" element={
            <>
              <Header title={"Usu치rios"}/>
              <Users users={users} handleUserRemove={handleUserRemove} />
            </>
          }/>
          <Route path='/:name/:email/:phone/:website' element={ <UserInfo /> } />
          <Route path='/add' element={ <AddUser handleUserAdd={handleUserAdd} /> } />
        </Routes>
      </div>
    </Router>
    </>

  );
};

export default App;
