import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { BrowserRouter as  Router, Route, Routes} from 'react-router-dom';


import './App.css';
import AddUser from './components/AddUser';
import Users from './components/Users';
import UserInfo from './components/UserInfo';

const App = () => {
  const [users, setUsers] = useState([
    {
      id: '1',
      name: 'Gabriel',
      email: 'g@gmail.com',
      tel: '9090',
      website: 'www.g.com.br'
    },
    {
      id: '2',
      name: 'Luna',
      email: 'l@gmail.com',
      tel: '1112',
      website: 'www.l.com.br'
    }
  ]);

  const handleUserAdd = (nameUser, emailUser, websiteUser, telefoneUser) => {
      const  newUsers = [...users, {
        id: uuidv4(),
        name: nameUser,
        email: emailUser,
        tel: telefoneUser,
        website: websiteUser
      },
    ];
    setUsers(newUsers)
  }

  const handleTaskRemove = (userId) => {
    const newUsers = users.filter(user => user.id !== userId)
    setUsers(newUsers)
  };
  return (
    <Router>
      <div className='container'>
        <Routes>
          <Route path="/" element={
            <>
              <Users users={users} handleTaskRemove={handleTaskRemove} />
              <AddUser handleUserAdd={handleUserAdd} />
            </>
          }/>
          <Route path='/:id' element={ <UserInfo /> } />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
