import './AddUser.css';
import Button from './Button';

const AddUser = () => {
    const [inputData, setInputData] = useState('');

    const handleInputChange = (e) => {
        console.log(e.target.value);
        setInputData(e.target.value)
    }


    return ( 
        <div className='add-user-container'>
            <input onChange={handleInputChange} type="text" className='add-user-input' placeholder='Nome' />
            <input onChange={handleInputChange} type="text" className='add-user-input' placeholder='Email'/>
            <input onChange={handleInputChange} type="text" className='add-user-input' placeholder='Telefone'/>
            <input onChange={handleInputChange} type="text" className='add-user-input' placeholder='Website'/>
            <div className='add-user-button-container'>
                <Button>Adicionar</Button>
            </div>
        </div>
     );
}

export default AddUser;
